import { createTheme } from '@mui/material/styles';
import { red } from '@mui/material/colors'; // Ví dụ màu

export const getAppTheme = (mode) => ({
  palette: {
    mode, // 'light' hoặc 'dark'
    ...(mode === 'light'
      ? {
          // Palette cho light mode
          primary: {
            main: '#1976d2', // Màu xanh dương cổ điển
          },
          secondary: {
            main: '#dc004e', // Màu hồng
          },
          background: {
            default: '#f4f6f8',
            paper: '#ffffff',
          },
          priorityHigh: {
            main: red[500],
            contrastText: '#fff',
          },
          priorityMedium: {
            main: '#ed6c02', // Orange
            contrastText: '#fff',
          },
          priorityLow: {
            main: '#2e7d32', // Green
            contrastText: '#fff',
          },
        }
      : {
          // Palette cho dark mode
          primary: {
            main: '#90caf9', // Xanh dương nhạt hơn cho dark mode
          },
          secondary: {
            main: '#f48fb1', // Hồng nhạt hơn
          },
          background: {
            default: '#121212', // Nền tối chuẩn
            paper: '#1e1e1e', // Nền giấy tối
          },
          text: {
            primary: '#e0e0e0',
            secondary: '#b0b0b0',
          },
          priorityHigh: {
            main: red[300],
            contrastText: '#000',
          },
          priorityMedium: {
            main: '#ffa726', // Orange nhạt
            contrastText: '#000',
          },
          priorityLow: {
            main: '#66bb6a', // Green nhạt
            contrastText: '#000',
          },
        }),
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 500,
    },
  },
  components: {
    // Tùy chỉnh mặc định cho một số component (ví dụ)
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none', // Không viết hoa tất cả chữ
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        }
      }
    }
  },
});
